import { contextBridge, ipcRenderer } from 'electron';
 
 // Expose protected methods that allow the renderer process to use
 // the ipcRenderer without exposing the entire object
 contextBridge.exposeInMainWorld('electron', {
     log: (level: string, message: string) => ipcRenderer.send('log', level, message),
     getVersion: () => process.versions.electron,
     
     // Auth API
     login: (credentials: { username: string; password: string; rememberMe?: boolean }) => 
       ipcRenderer.invoke('auth:login', credentials),
     verify2fa: (data: { code: string }) => ipcRenderer.invoke('auth:verify2fa', data),
     checkSession: () => ipcRenderer.invoke('auth:check-session'),
     autoLogin: () => ipcRenderer.invoke('auth:auto-login'),
     hasSavedCredentials: () => ipcRenderer.invoke('credentials:has-saved'),
     loadSavedCredentials: () => ipcRenderer.invoke('credentials:load'),
     logout: (options?: { clearSaved?: boolean }) => ipcRenderer.invoke('auth:logout', options || {}),
     clearCredentials: () => ipcRenderer.invoke('credentials:clear'),
     
     // Groups API
     getMyGroups: () => ipcRenderer.invoke('groups:get-my-groups'),
     getGroupDetails: (groupId: string) => ipcRenderer.invoke('groups:get-details', { groupId }),
     getGroupMembers: (groupId: string, offset = 0, n = 100) => ipcRenderer.invoke('groups:get-members', { groupId, offset, n }),
     getGroupRequests: (groupId: string) => ipcRenderer.invoke('groups:get-requests', { groupId }),
     getGroupBans: (groupId: string) => ipcRenderer.invoke('groups:get-bans', { groupId }),
     getGroupInstances: (groupId: string) => ipcRenderer.invoke('groups:get-instances', { groupId }),
     banUser: (groupId: string, userId: string) => ipcRenderer.invoke('groups:ban-user', { groupId, userId }),
     
     // Audit API
     getGroupAuditLogs: (groupId: string) => ipcRenderer.invoke('groups:get-audit-logs', { groupId }),

     // Worlds API
     getWorld: (worldId: string) => ipcRenderer.invoke('worlds:get-details', { worldId }),

     // Users API
     getUser: (userId: string) => ipcRenderer.invoke('users:get', { userId }),
     clearUserCache: (userId?: string) => ipcRenderer.invoke('users:clear-cache', { userId }),
     
     // Pipeline (WebSocket) API
     pipeline: {
       connect: () => ipcRenderer.invoke('pipeline:connect'),
       disconnect: () => ipcRenderer.invoke('pipeline:disconnect'),
       status: () => ipcRenderer.invoke('pipeline:status'),
       reconnect: () => ipcRenderer.invoke('pipeline:reconnect'),
       
       // Event listeners for real-time updates
       onEvent: (callback: (event: unknown) => void) => {
         const handler = (_event: Electron.IpcRendererEvent, data: unknown) => callback(data);
         ipcRenderer.on('pipeline:event', handler);
         return () => ipcRenderer.removeListener('pipeline:event', handler);
       },
       onConnected: (callback: (data: { connected: boolean }) => void) => {
         const handler = (_event: Electron.IpcRendererEvent, data: { connected: boolean }) => callback(data);
         ipcRenderer.on('pipeline:connected', handler);
         return () => ipcRenderer.removeListener('pipeline:connected', handler);
       },
       onDisconnected: (callback: (data: { code: number; reason: string; willReconnect: boolean }) => void) => {
         const handler = (_event: Electron.IpcRendererEvent, data: { code: number; reason: string; willReconnect: boolean }) => callback(data);
         ipcRenderer.on('pipeline:disconnected', handler);
         return () => ipcRenderer.removeListener('pipeline:disconnected', handler);
       },
       onError: (callback: (data: { message: string }) => void) => {
         const handler = (_event: Electron.IpcRendererEvent, data: { message: string }) => callback(data);
         ipcRenderer.on('pipeline:error', handler);
         return () => ipcRenderer.removeListener('pipeline:error', handler);
       },
     },

     // Log Watcher API
     logWatcher: {
       start: () => ipcRenderer.invoke('log-watcher:start'),
       stop: () => ipcRenderer.invoke('log-watcher:stop'),
       onPlayerJoined: (callback: (event: { displayName: string; userId?: string; timestamp: string }) => void) => {
         const handler = (_event: Electron.IpcRendererEvent, data: { displayName: string; userId?: string; timestamp: string }) => callback(data);
         ipcRenderer.on('log:player-joined', handler);
         return () => ipcRenderer.removeListener('log:player-joined', handler);
       },
       onPlayerLeft: (callback: (event: { displayName: string; userId?: string; timestamp: string }) => void) => {
         const handler = (_event: Electron.IpcRendererEvent, data: { displayName: string; userId?: string; timestamp: string }) => callback(data);
         ipcRenderer.on('log:player-left', handler);
         return () => ipcRenderer.removeListener('log:player-left', handler);
       },
       onLocation: (callback: (event: { worldId: string; timestamp: string }) => void) => {
         const handler = (_event: Electron.IpcRendererEvent, data: { worldId: string; timestamp: string }) => callback(data);
         ipcRenderer.on('log:location', handler);
         return () => ipcRenderer.removeListener('log:location', handler);
       },
       onWorldName: (callback: (event: { name: string; timestamp: string }) => void) => {
         const handler = (_event: Electron.IpcRendererEvent, data: { name: string; timestamp: string }) => callback(data);
         ipcRenderer.on('log:world-name', handler);
         return () => ipcRenderer.removeListener('log:world-name', handler);
       },
     },
     
     database: {
         getSessions: (groupId?: string) => ipcRenderer.invoke('database:get-sessions', groupId),
         getSessionEvents: (filename: string) => ipcRenderer.invoke('database:get-session-events', filename),
         clearSessions: () => ipcRenderer.invoke('database:clear-sessions'),
         rallyFromSession: (filename: string) => ipcRenderer.invoke('instance:rally-from-session', { filename }),
         onRallyProgress: (callback: (data: { sent: number; failed: number; total: number; done?: boolean }) => void) => {
             const handler = (_event: Electron.IpcRendererEvent, data: { sent: number; failed: number; total: number; done?: boolean }) => callback(data);
             ipcRenderer.on('rally:progress', handler);
             return () => ipcRenderer.removeListener('rally:progress', handler);
         },
     },
     
     // Window Controls
     minimize: () => ipcRenderer.invoke('window:minimize'),
     maximize: () => ipcRenderer.invoke('window:maximize'),
     close: () => ipcRenderer.invoke('window:close'),

     // Storage API
     storage: {
         getStatus: () => ipcRenderer.invoke('storage:get-status'),
         selectFolder: () => ipcRenderer.invoke('storage:select-folder'),
         setPath: (path: string) => ipcRenderer.invoke('storage:set-path', path),
     },

     // Instance Presence API
     instance: {
         getCurrentGroup: () => ipcRenderer.invoke('instance:get-current-group'),
         onGroupChanged: (callback: (groupId: string | null) => void) => {
             const handler = (_event: Electron.IpcRendererEvent, groupId: string | null) => callback(groupId);
             ipcRenderer.on('instance:group-changed', handler);
             return () => ipcRenderer.removeListener('instance:group-changed', handler);
         },
         // NEW LIVE OPS API
         scanSector: (groupId: string) => ipcRenderer.invoke('instance:scan-sector', { groupId }),
         recruitUser: (groupId: string, userId: string) => ipcRenderer.invoke('instance:recruit-user', { groupId, userId }),
         kickUser: (groupId: string, userId: string) => ipcRenderer.invoke('instance:kick-user', { groupId, userId }),
         // rallyForces: (groupId: string) => ipcRenderer.invoke('instance:rally-forces', { groupId }), // Deprecated but keeping for safety if needed
         getRallyTargets: (groupId: string) => ipcRenderer.invoke('instance:get-rally-targets', { groupId }),
         inviteToCurrent: (userId: string) => ipcRenderer.invoke('instance:invite-to-current', { userId }),
         closeInstance: () => ipcRenderer.invoke('instance:close-instance'),
         getInstanceInfo: () => ipcRenderer.invoke('instance:get-instance-info'),
         onEntityUpdate: (callback: (entity: any) => void) => {
             const handler = (_event: Electron.IpcRendererEvent, entity: any) => callback(entity);
             ipcRenderer.on('instance:entity-update', handler);
             return () => ipcRenderer.removeListener('instance:entity-update', handler);
         }
     },

     // Updater API
     updater: {
         onUpdateAvailable: (callback: () => void) => {
             const handler = () => callback();
             ipcRenderer.on('updater:update-available', handler);
             return () => ipcRenderer.removeListener('updater:update-available', handler);
         },
         onUpdateDownloaded: (callback: () => void) => {
             const handler = () => callback();
             ipcRenderer.on('updater:update-downloaded', handler);
             return () => ipcRenderer.removeListener('updater:update-downloaded', handler);
         },
         quitAndInstall: () => ipcRenderer.invoke('updater:quit-and-install'),
         checkStatus: () => ipcRenderer.invoke('updater:check-status')
     },
     
     // AutoMod API
     automod: {
         getRules: () => ipcRenderer.invoke('automod:get-rules'),
         saveRule: (rule: unknown) => ipcRenderer.invoke('automod:save-rule', rule),
         deleteRule: (ruleId: number) => ipcRenderer.invoke('automod:delete-rule', ruleId),
         checkUser: (user: unknown) => ipcRenderer.invoke('automod:check-user', user),
     }
 });

import { ipcMain, app } from 'electron';
import log from 'electron-log';
const logger = log.scope('AuthService');
import path from 'path';
import { saveCredentials, clearCredentials, loadCredentials, hasSavedCredentials } from './CredentialsService';
import { onUserLoggedIn, onUserLoggedOut } from './PipelineService';

// Import the VRChat SDK and Keyv for session persistence
// eslint-disable-next-line @typescript-eslint/no-require-imports
const { VRChat } = require('vrchat');
// eslint-disable-next-line @typescript-eslint/no-require-imports
const Keyv = require('keyv').default;
// eslint-disable-next-line @typescript-eslint/no-require-imports
const KeyvFile = require('keyv-file').default;

// Store the VRChat SDK instance in memory (Main Process)
let vrchatClient: InstanceType<typeof VRChat> | null = null;
let currentUser: Record<string, unknown> | null = null;
let pendingLoginCredentials: { username: string; password: string; rememberMe?: boolean; authCookie?: string } | null = null;

// Persistent session storage using Keyv with SQLite
let sessionStore: InstanceType<typeof Keyv> | null = null;

import { storageService } from './StorageService';

// ...

function getSessionStore(): InstanceType<typeof Keyv> {
  if (!sessionStore) {
    // Store sessions in the configured data directory
    const userDataPath = storageService.getDataDir();
    const filePath = path.join(userDataPath, 'vrchat-session.json');
    logger.info(`Session store path: ${filePath}`);
    
    const store = new KeyvFile({ filename: filePath });
    
    // WORKAROUND: Keyv v5+ crashes if store.opts.url is undefined during _checkIterableAdapter
    // We patch the store to satisfy Keyv's internal check
    if (!store.opts) store.opts = {};
    if (!store.opts.url) store.opts.url = 'file://';
    
    sessionStore = new Keyv({ store, namespace: 'vrchat' });
    
    // WORKAROUND 2: VRChat library might re-wrap our Keyv instance if it detects a version/instance mismatch.
    // This wrapper will check our instance's .opts.url, so we must ensure it exists.
    if (sessionStore.opts) {
        sessionStore.opts.url = 'file://';
    } else {
        // @ts-expect-error - forcing opts if missing
        sessionStore.opts = { url: 'file://' };
    }
    
    sessionStore.on('error', (err: Error) => {
      logger.error('Session store error:', err);
    });
  }
  return sessionStore;
}

// Application info for VRChat API User-Agent requirement
const APP_INFO = {
  name: 'VRChatGroupGuard',
  version: '1.0.0',
  contact: 'admin@groupguard.app'
};

// VRChat API base URL
const VRCHAT_API_BASE = 'https://api.vrchat.cloud/api/1';

// Helper to extract auth cookie from client
function extractAuthCookie(client: any): string | undefined {
  try {
    // Check various common places for cookies in HTTP clients
    const jar = client.jar || client.cookieJar || client.cookies;
    
    if (!jar) {
      logger.debug('No cookie jar found on client');
      return undefined;
    }

    let cookies: any[] = [];
    
    // If it's a tough-cookie jar or similar with getCookiesSync
    try {
      if (jar && typeof (jar as any).getCookiesSync === 'function') {
        cookies = (jar as any).getCookiesSync(VRCHAT_API_BASE);
      } else if (Array.isArray(jar)) {
        cookies = jar;
      } else if (jar._jar && typeof (jar._jar as any).getCookiesSync === 'function') {
        // tough-cookie wrapped
        cookies = (jar._jar as any).getCookiesSync(VRCHAT_API_BASE);
      }
    } catch (jarError) {
      logger.warn('Error accessing cookie jar:', jarError);
      return undefined;
    }

    if (!Array.isArray(cookies)) {
      logger.warn('Cookies is not an array:', typeof cookies);
      return undefined;
    }

    // Find the 'auth' cookie - be extremely defensive
    const authCookie = cookies.find((c: any) => {
      if (!c) return false;
      // Handle various cookie shapes (tough-cookie, electron cookie, simple object)
      const key = c.key || c.name; 
      if (!key) return false;
      
      // Safe check for 'auth'
      return key === 'auth' || (typeof key === 'string' && key.includes('auth'));
    });
    
    if (authCookie) {
      logger.debug('Found auth cookie');
      // Return in format key=value
      const key = authCookie.key || authCookie.name;
      const value = authCookie.value;
      return `${key}=${value}`;
    }
    
  } catch (err) {
    logger.warn('Failed to extract auth cookie:', err);
  }
  return undefined;
}

/**
 * Try to restore a session using the persisted Keyv session store
 * Returns the user if successful, null if the session is invalid/expired
 */
async function tryRestoreSession(): Promise<{
  success: boolean;
  user?: Record<string, unknown>;
  error?: string;
}> {
  try {
    logger.info('Attempting to restore session from persistent store...');
    
    // Create a client with the persistent session store
    // The SDK will automatically load any saved cookies from the Keyv store
    const clientOptions = {
      baseUrl: VRCHAT_API_BASE,
      application: APP_INFO,
      keyv: getSessionStore()
    };
    
    logger.info('Creating VRChat client for session check...');
    const client = new VRChat(clientOptions);
    
    // Try to get the current user - this will work if there's a valid session
    try {
      logger.info('Checking for existing session...');
      const userResponse = await client.getCurrentUser({ throwOnError: true });
      const user = userResponse?.data;
      
      if (user && user.id) {
        logger.info(`Session restored successfully for: ${user.displayName}`);
        
        // Store the client and user globally
        vrchatClient = client;
        
        // Sanitize ID
        if (user.id && typeof user.id === 'string') {
            user.id = user.id.trim();
        }
        
        currentUser = user as Record<string, unknown>;
        
        return { success: true, user: currentUser };
      }
      
      logger.info('No user data returned, session invalid');
      return { success: false, error: 'No user data' };
      
    } catch (err: unknown) {
      const error = err as { response?: { status?: number }; message?: string };
      
      // 401 = no valid session, this is expected on first launch
      if (error.response?.status === 401) {
        logger.info('No valid session found (401), will need to authenticate');
        return { success: false, error: 'No valid session' };
      }
      
      // Log and handle any other errors gracefully
      logger.warn('Session check failed with error:', error.message || String(err));
      return { success: false, error: error.message || 'Session check failed' };
    }
    
  } catch (error: unknown) {
    const err = error as { message?: string };
    logger.error('Session restoration error:', err.message || String(error));
    return { success: false, error: err.message || 'Session restoration failed' };
  }
}


/**
 * Internal login function - shared between manual and auto-login
 */
async function performLogin(username: string, password: string, twoFactorCode?: string): Promise<{
  success: boolean;
  user?: Record<string, unknown>;
  requires2FA?: boolean;
  twoFactorMethods?: string[];
  error?: string;
  authCookie?: string;
}> {
  try {
    logger.info('Attempting VRChat login...');
    logger.debug(`performLogin called for user ${username}`);

    // Create a fresh VRChat client instance
    let client = vrchatClient;
    
    // Create new client if needed
    if (!client || !twoFactorCode) {
         const clientOptions = {
            baseUrl: VRCHAT_API_BASE,
            application: APP_INFO,
            // Use Keyv for persistent session storage (cookies persist across restarts!)
            keyv: getSessionStore()
         };
         
         logger.info('Creating VRChat client with persistent session store for login...');
         client = new VRChat(clientOptions);
    }

    logger.info('VRChat client created (or reused), attempting login...');
    
    // Attempt login - the SDK's login method handles the authentication flow
    try {
      // If we have a cookie, we might strictly speaking purely verify credentials, 
      // but calling login ensure we get the user object and refresh session.
      // If the cookie is valid, login() should succeed without 2FA even if 2FA is enabled.
      
      const user = await client.login({ username, password, twoFactorCode });
      
      // ... User validation logic ...
      
      logger.debug('Login successful. Inspecting client for cookies...');
      
      // Extract and save new auth cookie
      const newAuthCookie = extractAuthCookie(client);
      if (newAuthCookie) {
         // Update the current credentials with the new cookie
         if (pendingLoginCredentials) {
            pendingLoginCredentials.authCookie = newAuthCookie;
         } else {
             // If manual login, we will save it in the auth:login handler
             // If auto-login, we should update the store
             // We can return it in the result or update global state? 
             // Best to just update the storage directly if we know who we are?
             // Actually, saveCredentials handles it.
         }
      }
      
      // ... (validation logic continues from previous file content)

      
      // Check if we got a valid user object
      let validUser: Record<string, unknown> = user;
      
      // ... (rest of validation) - REMOVED
      
      // Handle case where user is wrapped in data property
      // @ts-expect-error - Checking for data wrapper
      if (!validUser.id && validUser.data && validUser.data.id) {
         // @ts-expect-error - Unwrap data
         validUser = validUser.data;
      }
      
      // Check if it's an error response
      if (validUser.error) {
        // @ts-expect-error - Accessing error message
        throw new Error(validUser.error.message || 'Login returned an error');
      }
      
      // Validate we have an ID
      if (!validUser || !validUser.id) {
        logger.error('Login response missing ID:', validUser);
        throw new Error('Login failed: Invalid user object received');
      }

      // Success - store the client and user with Sanitized ID
      if (validUser.id && typeof validUser.id === 'string') {
          validUser.id = validUser.id.trim();
      }
      vrchatClient = client;
      currentUser = validUser;
      
      const userId = validUser.id as string;
      const displayName = validUser.displayName as string;
      
      logger.info(`User logged in successfully: ${displayName} (${userId})`);
      logger.debug('Login successful', { id: userId, name: displayName });
      
      logger.info(`Global vrchatClient set: ${!!vrchatClient}`);

      // Connect to Pipeline WebSocket for real-time events
      onUserLoggedIn();

      return { success: true, user: currentUser, authCookie: newAuthCookie };
      
    } catch (loginError: unknown) {
      // Check if this is a 2FA requirement
      const err = loginError as { message?: string; stack?: string; twoFactorMethods?: string[]; code?: string };
      
      const errorMsg = err?.message || 'Unknown login error';
      // Ensure errorMsg is a string before using string methods
      const errorMsgSafe = typeof errorMsg === 'string' ? errorMsg : String(errorMsg);
      const errorMsgLower = errorMsgSafe.toLowerCase();
      
      logger.info('Login error details:', {
        message: errorMsgSafe,
        twoFactorMethods: err?.twoFactorMethods,
        code: err?.code
      });
      
      // The SDK throws with twoFactorMethods array when 2FA is required
      if (err?.twoFactorMethods && Array.isArray(err.twoFactorMethods) && err.twoFactorMethods.length > 0) {
        logger.info('2FA required, methods:', err.twoFactorMethods);
        logger.debug('2FA required');
        
        // Store credentials and client for 2FA verification
        vrchatClient = client;
        
        return { 
          success: false, 
          requires2FA: true,
          twoFactorMethods: err.twoFactorMethods
        };
      }
      
      // WORKAROUND: If the library crashes with "Cannot read properties of undefined (reading 'includes')",
      // it is often due to a bug in handling the 2FA response (missing headers/cookies handling).
      // We assume this means 2FA is required if we haven't sent a code yet.
      if (errorMsgSafe.includes("Cannot read properties of undefined (reading 'includes')")) {
        logger.warn("Caught library crash compatible with 2FA response bug. Assuming 2FA required.");
        logger.debug("Stack trace:", err?.stack);
        
        vrchatClient = client;
        return { success: false, requires2FA: true };
      }

      // Check for common 2FA indicators in error message
      if (
        errorMsgLower.includes('two-factor') ||
        errorMsgLower.includes('2fa') ||
        errorMsgSafe.includes('TOTP') ||
        errorMsgSafe.includes('emailotp') ||
        errorMsgLower.includes('totp') ||
        errorMsgLower.includes('otp')
      ) {
        vrchatClient = client;
        logger.debug('2FA required (text check)');
        return { success: false, requires2FA: true };
      }
      
      
      // Re-throw for general error handling
      throw loginError;
    }

  } catch (error: unknown) {
    const err = error as { message?: string; stack?: string; response?: { data?: { error?: { message?: string } } } };
    logger.error('Login Failed (Outer Catch):', error);
    if (err && err.stack) {
        logger.error('Stack Trace:', err.stack);
    }
    
    // Extract meaningful error message
    let errorMessage = 'Unknown login error';
    
    if (err?.response?.data?.error?.message) {
      errorMessage = err.response.data.error.message;
    } else if (err?.message) {
      errorMessage = err.message;
    }
    
    if (typeof errorMessage !== 'string') {
        errorMessage = String(errorMessage);
    }
    
    // Append stack trace for debugging if available
    if (err?.stack) {
        errorMessage += `\n\nStack:\n${err.stack}`;
    }
    
    return { success: false, error: errorMessage };
  }
}

export function setupAuthHandlers() {
  
  // LOGIN Handler - accepts rememberMe flag
  ipcMain.handle('auth:login', async (_event, { username, password, rememberMe = false }: { 
    username: string; 
    password: string;
    rememberMe?: boolean;
  }) => {
    // Check if we have saved credentials that match these inputs
    const saved = loadCredentials();
    const isSavedUser = saved && saved.username === username;
    
    // If we have saved credentials for this user, try to restore session first to skip 2FA
    if (isSavedUser) {
        logger.info('Login matches saved user, attempting session restoration to bypass 2FA...');
        const restoreResult = await tryRestoreSession();
        
        // If restoration worked, we are logged in!
        // We do strictly verify the user ID to ensure we aren't using a stale cookie for the wrong account (though username check helps)
        if (restoreResult.success && restoreResult.user) {
             const restoredUser = restoreResult.user as { username?: string; displayName?: string };
             // Basic check to ensure it's the same person if possible (though API returns current user)
             logger.info('Session restored successfully during manual login!');
             return { success: true, user: restoreResult.user };
        }
    }
    
    // Fallback to standard login
    const result = await performLogin(username, password);
    
    if (result.success && rememberMe) {
      // Save credentials on successful direct login (no 2FA)
      // Save authCookie if we got one
      saveCredentials(username, password, result.authCookie);
      logger.info('Credentials saved for auto-login');
      logger.debug('Credentials saved manually');
    } else if (result.requires2FA) {
      // Store credentials for 2FA completion (will save after 2FA if rememberMe is set)
      // NOTE: We don't have authCookie yet usually for 2FA flow, but if we did we could store it
      pendingLoginCredentials = { username, password, rememberMe };
    }
    
    return result;
  });

  // 2FA Verification Handler
  ipcMain.handle('auth:verify2fa', async (_event, { code }: { code: string }) => {
    if (!vrchatClient || !pendingLoginCredentials) {
      return { success: false, error: "No pending login session. Please try logging in again." };
    }
    
    try {
      logger.info('Verifying 2FA code...');
      
      const result = await performLogin(
        pendingLoginCredentials.username,
        pendingLoginCredentials.password,
        code
      );

      if (!result.success || !result.user) {
        throw new Error(result.error || '2FA verification failed');
      }

      // Save credentials if rememberMe was set during initial login
      if (pendingLoginCredentials.rememberMe) {
        // Save with the new authCookie from the result
        saveCredentials(pendingLoginCredentials.username, pendingLoginCredentials.password, result.authCookie);
        logger.info('Credentials saved for auto-login after 2FA');
        logger.debug('Credentials saved after 2FA');
      }
      
      pendingLoginCredentials = null; // Clear pending credentials
      
      // Note: performLogin sets currentUser and vrchatClient already
      
      return { success: true, user: currentUser };
      
    } catch (error: unknown) {
      const err = error as { message?: string };
      logger.error("2FA Verification Error:", error);
      
      const errorMessage = err.message || 'Invalid 2FA code';
      
      // Check for specific error types
      if (errorMessage.toLowerCase().includes('invalid') || errorMessage.toLowerCase().includes('incorrect')) {
        return { success: false, error: 'Invalid 2FA code. Please try again.' };
      }
      
      return { success: false, error: errorMessage };
    }
  });

  // AUTO-LOGIN Handler - attempts login with saved credentials
  ipcMain.handle('auth:auto-login', async () => {
    logger.info('Checking for saved credentials for auto-login...');
    
    if (!hasSavedCredentials()) {
      logger.info('No saved credentials found');
      return { success: false, noCredentials: true };
    }
    
    const credentials = loadCredentials();
    if (!credentials) {
      logger.info('Failed to load credentials');
      return { success: false, error: 'Failed to load saved credentials' };
    }
    
    logger.info('Found saved credentials, attempting session restoration...');
    
    // FIRST: Try to restore session from Keyv store (no 2FA required!)
    const sessionResult = await tryRestoreSession();
    
    if (sessionResult.success && sessionResult.user) {
      logger.info('Session restored successfully without re-authentication!');
      return { success: true, user: sessionResult.user };
    }
    
    logger.info('Session restoration failed, falling back to full login...');
    
    // FALLBACK: Full login (will require 2FA if enabled)
    logger.info(`Attempting full login for ${credentials.username}...`);
    const result = await performLogin(credentials.username, credentials.password);
    
    if (result.success) {
       // Update the cookie if it changed
       if (result.authCookie && result.authCookie !== credentials.authCookie) {
          saveCredentials(credentials.username, credentials.password, result.authCookie);
          logger.debug('Auth cookie updated after auto-login');
       }
    }
    
    if (result.requires2FA) {
      // Store pending credentials with rememberMe for 2FA
      pendingLoginCredentials = { 
        username: credentials.username, 
        password: credentials.password, 
        rememberMe: true,
        authCookie: credentials.authCookie
      };
    }
    
    return result;
  });

  // Check Session - returns current user if logged in
  ipcMain.handle('auth:check-session', () => {
    if (currentUser && vrchatClient) {
      return { isLoggedIn: true, user: currentUser };
    }
    return { isLoggedIn: false };
  });
  
  // Check if saved credentials exist
  ipcMain.handle('auth:has-saved-credentials', () => {
    return hasSavedCredentials();
  });

  // Logout Handler - optionally clears saved credentials
  ipcMain.handle('auth:logout', async (_event, { clearSaved = false }: { clearSaved?: boolean } = {}) => {
    try {
      // The SDK may have a logout method, but we mainly need to clear local state
      // VRChat doesn't have a traditional logout endpoint - sessions are cookie-based
      logger.info('Logging out user...');
      logger.debug('Logging out');
      
      if (clearSaved) {
        clearCredentials();
        logger.info('Saved credentials cleared');
        logger.debug('Saved credentials cleared');
      }
    } catch (e) {
      logger.warn('Logout cleanup:', e);
    }
    
    vrchatClient = null;
    currentUser = null;
    pendingLoginCredentials = null;
    
    // Disconnect from Pipeline WebSocket
    onUserLoggedOut();
    
    return { success: true };
  });
}

// Helper to share client with other services (Groups, Audit, etc.)
export function getVRChatClient() {
  logger.debug(`getVRChatClient called. Result exists: ${!!vrchatClient}`);
  return vrchatClient;
}

// Helper to check if authenticated
export function isAuthenticated(): boolean {
  return vrchatClient !== null && currentUser !== null;
}

// Helper to get current user's ID
export function getCurrentUserId(): string | null {
  logger.debug(`getCurrentUserId called. ID: ${currentUser?.id}`);
  logger.debug('Full currentUser keys:', Object.keys(currentUser || {}));
  return currentUser?.id as string | null;
}

import { ipcMain, safeStorage } from 'electron';
import log from 'electron-log';
const logger = log.scope('CredentialsService');

// electron-store is ESM in v9+, we need to use dynamic import or require
// eslint-disable-next-line @typescript-eslint/no-require-imports
const Store = require('electron-store');

// Schema for our stored data
interface StoredCredentials {
  username: string;
  // Password is encrypted using Electron's safeStorage
  encryptedPassword: string;
  // Auth cookie from VRChat for session restoration
  authCookie?: string;
}

// Initialize electron-store with encryption
// Using any type since electron-store types are complex with ESM/CJS interop
// eslint-disable-next-line @typescript-eslint/no-explicit-any
const store: any = new Store({
  name: 'group-guard-credentials',
  encryptionKey: 'group-guard-secure-storage-v1', // Additional layer of encryption
  defaults: {
    rememberMe: false
  }
});

/**
 * Save credentials securely
 * Uses Electron's safeStorage for password encryption when available
 */
export function saveCredentials(username: string, password: string, authCookie?: string): boolean {
  try {
    let encryptedPassword: string;
    
    // Use safeStorage if available (uses OS keychain/credential manager)
    if (safeStorage.isEncryptionAvailable()) {
      const encrypted = safeStorage.encryptString(password);
      encryptedPassword = encrypted.toString('base64');
    } else {
      // Fallback to base64 (less secure, but electron-store already encrypts)
      encryptedPassword = Buffer.from(password).toString('base64');
      log.warn('safeStorage not available, using fallback encryption');
    }
    
    const credentials: StoredCredentials = {
      username,
      encryptedPassword,
      authCookie
    };
    
    store.set('savedCredentials', credentials);
    store.set('rememberMe', true);
    
    log.info('Credentials saved securely');
    return true;
  } catch (error) {
    log.error('Failed to save credentials:', error);
    return false;
  }
}

/**
 * Load saved credentials
 */
export function loadCredentials(): { username: string; password: string; authCookie?: string } | null {
  try {
    const saved = store.get('savedCredentials') as StoredCredentials | undefined;
    if (!saved) {
      return null;
    }
    
    let password: string;
    
    // Decrypt using safeStorage if available
    if (safeStorage.isEncryptionAvailable()) {
      try {
        const encrypted = Buffer.from(saved.encryptedPassword, 'base64');
        password = safeStorage.decryptString(encrypted);
      } catch {
        // If decryption fails (e.g., different machine), try base64 fallback
        password = Buffer.from(saved.encryptedPassword, 'base64').toString('utf-8');
      }
    } else {
      // Fallback from base64
      password = Buffer.from(saved.encryptedPassword, 'base64').toString('utf-8');
    }
    
    return {
      username: saved.username,
      password,
      authCookie: saved.authCookie
    };
  } catch (error) {
    log.error('Failed to load credentials:', error);
    return null;
  }
}

/**
 * Update the auth cookie for saved credentials
 */
export function updateAuthCookie(authCookie: string): boolean {
  try {
    const saved = store.get('savedCredentials') as StoredCredentials | undefined;
    if (!saved) {
      return false;
    }
    
    store.set('savedCredentials', {
      ...saved,
      authCookie
    });
    
    log.info('Auth cookie updated');
    return true;
  } catch (error) {
    log.error('Failed to update auth cookie:', error);
    return false;
  }
}

/**
 * Clear all saved credentials
 */
export function clearCredentials(): void {
  try {
    store.delete('savedCredentials');
    store.set('rememberMe', false);
    log.info('Credentials cleared');
  } catch (error) {
    log.error('Failed to clear credentials:', error);
  }
}

/**
 * Check if credentials are saved
 */
export function hasSavedCredentials(): boolean {
  return store.has('savedCredentials') && store.get('rememberMe', false);
}

/**
 * Get the remember me preference
 */
export function getRememberMe(): boolean {
  return store.get('rememberMe', false);
}

/**
 * Setup IPC handlers for credentials
 */
export function setupCredentialsHandlers() {
  // Check if credentials are saved
  ipcMain.handle('credentials:has-saved', () => {
    return hasSavedCredentials();
  });
  
  // Get remember me state
  ipcMain.handle('credentials:get-remember-me', () => {
    return getRememberMe();
  });
  
  // Save credentials (called after successful login)
  ipcMain.handle('credentials:save', (_event, { username, password, authCookie }: { 
    username: string; 
    password: string; 
    authCookie?: string 
  }) => {
    return saveCredentials(username, password, authCookie);
  });
  
  // Clear credentials
  ipcMain.handle('credentials:clear', () => {
    clearCredentials();
    return true;
  });
  
  // Load credentials for auto-login
  ipcMain.handle('credentials:load', () => {
    return loadCredentials();
  });
  
  log.info('Credentials handlers initialized');
}

import { ipcMain } from 'electron';
import log from 'electron-log';
const logger = log.scope('GroupService');
import { getVRChatClient, getCurrentUserId } from './AuthService';

export function setupGroupHandlers() {

  // Get user's groups (groups where user is a member)
  ipcMain.handle('groups:get-my-groups', async () => {
    try {
      const client = getVRChatClient();
      const userId = getCurrentUserId();
      
      logger.debug('groups:get-my-groups called', { hasClient: !!client, userId });
      
      if (!client || !userId) {
        logger.warn('Auth check failed in GroupService');
        throw new Error("Not authenticated. Please log in first.");
      }

      logger.info(`Fetching user groups for user ID: "${userId}" (type: ${typeof userId})`);
      
      // Sanitize userId
      const safeUserId = userId.trim();
      if (!safeUserId.startsWith('usr_')) {
          logger.error(`Invalid User ID format: ${safeUserId}`);
          throw new Error(`Invalid User ID: ${safeUserId}`);
      }
      
      // Reverting to Object Syntax as positional caused "malformed url"
      const response = await client.getUserGroups({ 
        path: { userId: safeUserId },
        query: { n: 100, offset: 0 }
      });

      if (response.error) {
        logger.error('getUserGroups returned error:', response.error);
        throw new Error((response.error as { message?: string }).message || 'Failed to fetch groups');
      }

      const groups = response.data || [];
      
      // Filter for groups where user has moderation powers
      const moderatableGroups = groups.filter((g: any) => {
        const isOwner = g.ownerId === safeUserId;
        const hasPermissions = g.myMember?.permissions && Array.isArray(g.myMember.permissions) && g.myMember.permissions.length > 0;
        return isOwner || hasPermissions;
      });

      // map the groups to ensure 'id' is the Group ID (grp_), not the Member ID (gmem_)
      const mappedGroups = moderatableGroups.map((g: any) => {
        // VRChat API getUserGroups returns membership objects.
        // g.id is the Membership ID (gmem_...)
        // g.groupId is the actual Group ID (grp_...)
        // We want the frontend to see 'id' as the Group ID.
        if (g.groupId && typeof g.groupId === 'string' && g.groupId.startsWith('grp_')) {
            return {
                ...g,
                id: g.groupId,      // helper for frontend
                _memberId: g.id     // preserve original membership ID
            };
        }
        return g;
      });

      logger.info(`Fetched ${groups.length} total groups. Filtered to ${mappedGroups.length} moderatable groups.`);
      
      // Update InstanceLoggerService with allowed groups
      try {
          const { instanceLoggerService } = require('./InstanceLoggerService');
          instanceLoggerService.setAllowedGroups(mappedGroups.map(g => g.id));
      } catch (e) {
          logger.error('Failed to update instance logger allowed groups', e);
      }

      return { success: true, groups: mappedGroups };

    } catch (error: unknown) {
      const err = error as { message?: string; response?: { status?: number }; stack?: string; config?: unknown };
      logger.error('Failed to fetch groups:', { message: err.message, stack: err.stack });
      if (err.response?.status === 401) return { success: false, error: 'Session expired. Please log in again.' };
      return { success: false, error: err.message || 'Failed to fetch groups' };
    }
  });

  // Get specific group details
  ipcMain.handle('groups:get-details', async (_event, { groupId }: { groupId: string }) => {
    try {
      const client = getVRChatClient();
      if (!client) throw new Error("Not authenticated");
  
      // Revert to Object Syntax
      const response = await client.getGroup({ path: { groupId } });
      
      if (response.error) throw response.error;
      return { success: true, group: response.data };
      
    } catch (error: unknown) {
      const err = error as { message?: string };
      logger.error('Failed to fetch group details:', error);
      return { success: false, error: err.message || 'Failed to fetch group' };
    }
  });

  // Get world details
  ipcMain.handle('worlds:get-details', async (_event, { worldId }: { worldId: string }) => {
    try {
      const client = getVRChatClient();
      if (!client) throw new Error("Not authenticated");
  
      // Revert to Object Syntax
      const response = await client.getWorld({ path: { worldId } });
      
      if (response.error) throw response.error;
      return { success: true, world: response.data };
      
    } catch (error: unknown) {
      const err = error as { message?: string };
      logger.error('Failed to fetch world details:', error);
      return { success: false, error: err.message || 'Failed to fetch world' };
    }
  });

  // Get group members
  ipcMain.handle('groups:get-members', async (_event, { groupId, n = 100, offset = 0 }: { groupId: string; n?: number; offset?: number }) => {
    try {
      const client = getVRChatClient();
      if (!client) throw new Error("Not authenticated");
  
      // Revert to Object Syntax
      const response = await client.getGroupMembers({ 
        path: { groupId },
        query: { n, offset }
      });
      
      if (response.error) throw response.error;
      return { success: true, members: response.data ?? [] };
      
    } catch (error: unknown) {
      const err = error as { message?: string };
      logger.error('Failed to fetch group members:', error);
      return { success: false, error: err.message || 'Failed to fetch members' };
    }
  });

  // Helper to extract array from VRChat API response
  // Some endpoints return Array, others return { results: Array } or { instances: Array }
  const extractArray = (data: unknown): unknown[] => {
      if (Array.isArray(data)) return data;
      const obj = data as Record<string, unknown> | null;
      if (obj && Array.isArray(obj.results)) return obj.results;
      if (obj && Array.isArray(obj.instances)) return obj.instances;
      return [];
  };

  // Get group join requests
  ipcMain.handle('groups:get-requests', async (_event, { groupId }: { groupId: string }) => {
    try {
      const client = getVRChatClient();
      logger.info(`Fetching requests for group ${groupId}`);
      if (!client) throw new Error("Not authenticated");
  
      // Revert to Object Syntax
      const response = await client.getGroupRequests({ 
          path: { groupId },
          query: { n: 100, offset: 0 }
      });
      
      const requests = extractArray(response.data);
      logger.info(`Requests fetch detected ${requests.length} items for ${groupId}`);
      
      if (response.error) {
        logger.error('API Error in getGroupRequests:', response.error);
        throw response.error;
      }
      return { success: true, requests };
      
    } catch (error: unknown) {
      const err = error as { message?: string };
      logger.error('Failed to fetch join requests:', error);
      return { success: false, error: err.message || 'Failed to fetch requests' };
    }
  });

  // Get group bans
  ipcMain.handle('groups:get-bans', async (_event, { groupId }: { groupId: string }) => {
    try {
      const client = getVRChatClient();
      logger.info(`Fetching bans for group ${groupId}`);
      if (!client) throw new Error("Not authenticated");
  
      // Revert to Object Syntax
      const response = await client.getGroupBans({ 
        path: { groupId },
        query: { n: 100, offset: 0 }
      });
      
      const bans = extractArray(response.data);
      logger.info(`Bans fetch detected ${bans.length} items for ${groupId}`);

      if (response.error) {
         logger.error('API Error in getGroupBans:', response.error);
         throw response.error;
      }
      return { success: true, bans };
      
    } catch (error: unknown) {
      const err = error as { message?: string };
      logger.error('Failed to fetch bans:', error);
      return { success: false, error: err.message || 'Failed to fetch bans' };
    }
  });

  // Get group audit logs
  ipcMain.handle('groups:get-audit-logs', async (_event, { groupId }: { groupId: string }) => {
    try {
      const client = getVRChatClient();
      if (!client) throw new Error("Not authenticated");
      
      // Revert to Object Syntax
      const response = await client.getGroupAuditLogs({ 
          path: { groupId },
          query: { n: 100, offset: 0 }
      });
      
      if (response.error) throw response.error;
      const logs = extractArray(response.data);
      return { success: true, logs };
      
    } catch (error: unknown) {
      const err = error as { message?: string };
      logger.error('Failed to fetch audit logs:', error);
      return { success: false, error: err.message || 'Failed to fetch audit logs' };
    }
  });

  // Get active group instances - using direct HTTP to bypass SDK quirks
  ipcMain.handle('groups:get-instances', async (_event, { groupId }: { groupId: string }) => {
    // Helper to safely stringify objects with BigInt values
    const safeStringify = (obj: unknown): string => {
      try {
        return JSON.stringify(obj, (_key, value) => 
          typeof value === 'bigint' ? value.toString() : value
        );
      } catch {
        return String(obj);
      }
    };

    try {
      const client = getVRChatClient();
      if (!client) throw new Error("Not authenticated");
      
      const userId = getCurrentUserId();
      if (!userId) throw new Error("No user ID found");

      logger.info(`[INSTANCES] Fetching for group: ${groupId}, user: ${userId}`);

      // Strategy 1: Try the SDK method getUserGroupInstancesForGroup
      let instances: unknown[] = [];
      
      try {
        const clientAny = client as Record<string, unknown>;
        if (typeof clientAny.getUserGroupInstancesForGroup === 'function') {
          logger.info('[INSTANCES] Trying SDK method: getUserGroupInstancesForGroup');
          const response = await (clientAny.getUserGroupInstancesForGroup as CallableFunction)({ 
            path: { userId, groupId } 
          });
          const data = (response as { data?: unknown })?.data ?? response;
          logger.info('[INSTANCES] SDK Response:', safeStringify(data));
          instances = extractArray(data);
        } else {
          logger.warn('[INSTANCES] SDK method getUserGroupInstancesForGroup not available');
        }
      } catch (e: unknown) {
        const err = e as { message?: string };
        logger.warn('[INSTANCES] SDK getUserGroupInstancesForGroup failed:', err.message);
      }

      // Strategy 2: Try getUserGroupInstances (all groups) and filter
      if (instances.length === 0) {
        try {
          const clientAny = client as Record<string, unknown>;
          if (typeof clientAny.getUserGroupInstances === 'function') {
            logger.info('[INSTANCES] Trying SDK method: getUserGroupInstances (all groups)');
            const response = await (clientAny.getUserGroupInstances as CallableFunction)({ 
              path: { userId } 
            });
            const data = (response as { data?: unknown })?.data ?? response;
            const allInstances = extractArray(data);
            logger.info(`[INSTANCES] getUserGroupInstances returned ${allInstances.length} total instances`);
            
            if (allInstances.length > 0) {
              logger.info('[INSTANCES] First instance keys:', Object.keys(allInstances[0] as object));
              logger.info('[INSTANCES] First instance data:', safeStringify(allInstances[0]));
              
              // Try multiple filter strategies
              instances = allInstances.filter((inst: unknown) => {
                const i = inst as Record<string, unknown>;
                const matchGroupId = i.groupId === groupId;
                const matchGroupObj = (i.group as Record<string, unknown>)?.id === groupId;
                const matchOwnerId = String(i.ownerId || '').includes(groupId);
                return matchGroupId || matchGroupObj || matchOwnerId;
              });
              logger.info(`[INSTANCES] After filtering: ${instances.length} instances for this group`);
            }
          }
        } catch (e: unknown) {
          const err = e as { message?: string };
          logger.warn('[INSTANCES] SDK getUserGroupInstances failed:', err.message);
        }
      }

      // Strategy 3: Use client.get if available  
      if (instances.length === 0) {
        try {
          const clientAny = client as Record<string, unknown>;
          if (typeof clientAny.get === 'function') {
            logger.info('[INSTANCES] Trying client.get fallback');
            
            // Try specific group endpoint first
            const url = `users/${userId}/instances/groups/${groupId}`;
            logger.info('[INSTANCES] Calling:', url);
            const response = await (clientAny.get as CallableFunction)(url);
            const data = (response as { data?: unknown })?.data ?? response;
            logger.info('[INSTANCES] client.get response:', safeStringify(data));
            instances = extractArray(data);
          }
        } catch (e: unknown) {
          const err = e as { message?: string };
          logger.warn('[INSTANCES] client.get failed:', err.message);
        }
      }

      // Strategy 4: Try the getGroupInstances method (different from user-specific)
      if (instances.length === 0) {
        try {
          const clientAny = client as Record<string, unknown>;
          if (typeof clientAny.getGroupInstances === 'function') {
            logger.info('[INSTANCES] Trying SDK method: getGroupInstances');
            const response = await (clientAny.getGroupInstances as CallableFunction)({ 
              path: { groupId } 
            });
            const data = (response as { data?: unknown })?.data ?? response;
            logger.info('[INSTANCES] getGroupInstances response:', safeStringify(data));
            instances = extractArray(data);
          }
        } catch (e: unknown) {
          const err = e as { message?: string };
          logger.warn('[INSTANCES] SDK getGroupInstances failed:', err.message);
        }
      }

      logger.info(`[INSTANCES] Final result: ${instances.length} instances for group ${groupId}`);
      
      if (instances.length > 0) {
        logger.info('[INSTANCES] Sample instance:', safeStringify(instances[0]));
      }
      
      return { success: true, instances };
      
    } catch (error: unknown) {
      const err = error as { message?: string; stack?: string };
      logger.error('[INSTANCES] Fatal error:', err.message);
      logger.error('[INSTANCES] Stack:', err.stack);
      return { success: false, error: err.message || 'Failed to fetch instances' };
    }
  });

  // Ban a user from a group
  ipcMain.handle('groups:ban-user', async (_event, { groupId, userId }: { groupId: string; userId: string }) => {
    const client = getVRChatClient();
    if (!client) throw new Error("Not authenticated");

    try {
      logger.info(`[GroupService] Banning user ${userId} from group ${groupId}`);
      
      // Use correct SDK syntax with path and body parameters
      const response = await client.banGroupMember({ 
        path: { groupId }, 
        body: { userId } 
      });
      
      if (response.error) {
        logger.error(`[GroupService] Ban API returned error:`, response.error);
        const errorMessage = (response.error as { message?: string }).message || 'Ban failed';
        return { success: false, error: errorMessage };
      }
      
      logger.info(`[GroupService] Successfully banned user ${userId} from group ${groupId}`);
      return { success: true };
    } catch (e: unknown) {
      const err = e as { message?: string; response?: { data?: { error?: { message?: string } } } };
      logger.error(`[GroupService] Failed to ban user ${userId} from group ${groupId}:`, e);
      const msg = err.response?.data?.error?.message || err.message || 'Unknown error';
      return { success: false, error: msg };
    }
  });
}

import { app, ipcMain, BrowserWindow } from 'electron';
import log from 'electron-log';
const logger = log.scope('LogWatcher');
import fs from 'fs';
import path from 'path';
import { EventEmitter } from 'events';

// ============================================
// TYPES
// ============================================

export interface LogEvent {
  type: 'player-joined' | 'player-left' | 'location' | 'world-name' | 'destination';
  timestamp: string;
  data: Record<string, string>;
}

export interface PlayerJoinedEvent {
  displayName: string;
  userId?: string; 
  timestamp: string;
}

export interface LocationEvent {
  worldId: string;
  worldName?: string;
  timestamp: string;
}

// ============================================
// STATE CACHE
// ============================================

interface WatcherState {
  currentWorldId: string | null;
  currentWorldName: string | null;
  currentLocation: string | null; // Full location string for proper tracking
  players: Map<string, PlayerJoinedEvent>; // keyed by displayName
}

// ============================================
// SERVICE
// ============================================

class LogWatcherService extends EventEmitter {
  private currentLogPath: string | null = null;
  private currentFileSize = 0;
  private watcherInterval: NodeJS.Timeout | null = null;
  private isWatching = false;
  
  // State for late joiners
  private state: WatcherState = {
    currentWorldId: null,
    currentWorldName: null,
    currentLocation: null,
    players: new Map()
  };

  /**
   * Start watching. Validates directory, finds latest log, and starts trailing.
   * If callerWindow is provided, syncs current state to it immediately.
   */
  start(callerWindow?: BrowserWindow) {
    // If requested by a specific window, send it the current state immediately
    if (callerWindow && !callerWindow.isDestroyed()) {
        this.emitStateToWindow(callerWindow);
    }

    if (this.isWatching) {
        log.info('[LogWatcher] Service already running, synced state to requestor.');
        return;
    }

    this.isWatching = true;
    log.info('[LogWatcher] Starting service...');
    
    this.findLatestLog();
    
    this.watcherInterval = setInterval(() => {
      this.checkLogPath();
      this.readNewContent();
    }, 1000);
  }

  stop() {
    this.isWatching = false;
    if (this.watcherInterval) {
      clearInterval(this.watcherInterval);
      this.watcherInterval = null;
    }
    log.info('[LogWatcher] Service stopped');
  }

  private emitStateToWindow(window: BrowserWindow) {
     log.info('[LogWatcher] Syncing state to renderer...');
     const timestamp = new Date().toISOString().replace('T', ' ').substring(0, 19).replace(/-/g, '.');

     if (this.state.currentWorldName) {
         window.webContents.send('log:world-name', { name: this.state.currentWorldName, timestamp });
     }
     if (this.state.currentWorldId) {
         window.webContents.send('log:location', { worldId: this.state.currentWorldId, timestamp });
     }
     
     // Send all known players
     for (const player of this.state.players.values()) {
         window.webContents.send('log:player-joined', player);
     }
  }

  private getLogDirectory(): string {
    const appData = app.getPath('appData');
    const localLow = path.join(appData, '..', 'LocalLow');
    return path.join(localLow, 'VRChat', 'VRChat');
  }

  private findLatestLog() {
    try {
      const logDir = this.getLogDirectory();
      if (!fs.existsSync(logDir)) {
        log.warn(`[LogWatcher] VRChat log directory not found: ${logDir}`);
        return;
      }

      const files = fs.readdirSync(logDir)
        .filter(f => f.startsWith('output_log_') && f.endsWith('.txt'))
        .map(f => {
          const fullPath = path.join(logDir, f);
          return {
            name: f,
            path: fullPath,
            stat: fs.statSync(fullPath)
          };
        })
        .sort((a, b) => b.stat.mtime.getTime() - a.stat.mtime.getTime());

      if (files.length > 0) {
        const latest = files[0];
        if (latest.path !== this.currentLogPath) {
          log.info(`[LogWatcher] Found new log file: ${latest.name}`);
          this.currentLogPath = latest.path;
          // Always read from 0 on new file detection to build state
          this.currentFileSize = 0; 
          // Reset state for new log (new session)
          this.state = { currentWorldId: null, currentWorldName: null, currentLocation: null, players: new Map() };
        }
      }
    } catch (error) {
      log.error('[LogWatcher] Error searching for logs:', error);
    }
  }

  private checkLogPath() {
    this.findLatestLog();
  }

  private readNewContent() {
    if (!this.currentLogPath) return;

    try {
      if (!fs.existsSync(this.currentLogPath)) return;

      const stat = fs.statSync(this.currentLogPath);
      if (stat.size > this.currentFileSize) {
        const stream = fs.createReadStream(this.currentLogPath, {
          start: this.currentFileSize,
          end: stat.size
        });

        let buffer = '';
        stream.on('data', (chunk) => { buffer += chunk.toString(); });

        stream.on('end', () => {
          this.currentFileSize = stat.size;
          const lines = buffer.split('\n');
          for (const line of lines) {
            if (line.trim()) this.parseLine(line.trim());
          }
        });
      }
    } catch (err) {
      log.error('[LogWatcher] Error reading log:', err);
    }
  }

  private parseLine(line: string) {
    // Timestamp check: yyyy.MM.dd HH:mm:ss
    const timestamp = line.substring(0, 19);
    
    // Regex Definitions (based on FCH / VRCX)
    // 1. Joining World: "Joining wrld_..."
    // Matches "Joining wrld_ID:instanceId~tag(val)..."
    // Use non-greedy for instance ID until next space or end of line?
    // Actually, VRChat log lines look like: "[Always] Joining wrld_xxx:12345~group(grp_xxx)"
    const reJoining = /Joining\s+(wrld_[a-zA-Z0-9-]+):([^\s]+)/;

    // 2. Player Joined: "OnPlayerJoined Name (usr_...)" - handles prefixes
    const rePlayerJoined = /OnPlayerJoined\s+(?:\[[^\]]+\]\s*)?([^\r\n(]+?)\s*\((usr_[a-f0-9-]{36})\)/;
    // 3. Player Left: "OnPlayerLeft Name (usr_...)"
    const rePlayerLeft = /OnPlayerLeft\s+([^\r\n(]+?)\s*\((usr_[a-f0-9-]{36})\)/;
    // 4. Entering Room (World Name) - usually has [Behaviour]
    const reEntering = /\[Behaviour\] Entering Room: (.+)/;

    // 5. Avatar Change
    const reAvatar = /\[Avatar\] Loading Avatar:\s+(avtr_[a-f0-9-]{36})/;

    // ...

    // 1. World Location (Joining wrld_...)
    const joinMatch = line.match(reJoining);
    if (joinMatch) {
         const worldId = joinMatch[1];
        const fullInstanceString = joinMatch[2]; // Includes tags like 12345~group(...)
        // IMPORTANT: Use the FULL instance string for API calls (includes ~group, ~region, etc.)
        // The API needs this to identify group instances for permissions
        const instanceId = fullInstanceString; // Keep the full string!
        const location = `${worldId}:${fullInstanceString}`;
        
        log.info(`[LogWatcher] MATCH Joining: ${location}`);
        
        // CRITICAL: Check if FULL LOCATION changed (not just worldId!)
        // This ensures we track instance changes even within the same world
        if (this.state.currentLocation !== location) {
            log.info(`[LogWatcher] Location CHANGED from ${this.state.currentLocation} to ${location}`);
            
            // Clear players only if world changed (same world, different instance = keep players briefly)
            if (this.state.currentWorldId !== worldId) {
                this.state.players.clear();
            }
            
            this.state.currentWorldId = worldId;
            this.state.currentLocation = location;
            
            // Emit full location info - instanceId now includes all tags
            this.emitToRenderer('log:location', { worldId, instanceId, location, timestamp });
            this.emit('location', { worldId, instanceId, location, timestamp });
        }
    }

    // 5. Avatar Change
    const avatarMatch = line.match(reAvatar);
    if (avatarMatch) {
       const avatarId = avatarMatch[1];
       log.info(`[LogWatcher] MATCH Avatar: ${avatarId}`);
       this.emitToRenderer('log:avatar', { avatarId, timestamp });
       this.emit('avatar', { avatarId, timestamp });
    }

    // 2. World Name (Entering Room: ...)
    const enterMatch = line.match(reEntering);
    if (enterMatch) {
        const worldName = enterMatch[1].trim();
        log.info(`[LogWatcher] MATCH Entering Room: ${worldName}`);
        this.state.currentWorldName = worldName;
        this.emitToRenderer('log:world-name', { name: worldName, timestamp });
        this.emit('world-name', { name: worldName, timestamp });
    }

    // 3. Player Joined
    const playerJoinMatch = line.match(rePlayerJoined);
    if (playerJoinMatch) {
        const displayName = playerJoinMatch[1].trim();
        const userId = playerJoinMatch[2];
        
        log.info(`[LogWatcher] MATCH Player Joined: ${displayName} (${userId})`);
        
        const playerEvent: PlayerJoinedEvent = { displayName, userId, timestamp };
        this.state.players.set(displayName, playerEvent);
        this.emitToRenderer('log:player-joined', playerEvent);
        this.emit('player-joined', playerEvent);
    }

    // 4. Player Left
    const playerLeftMatch = line.match(rePlayerLeft);
    if (playerLeftMatch) {
        const displayName = playerLeftMatch[1].trim();
        const userId = playerLeftMatch[2];
        
        log.info(`[LogWatcher] MATCH Player Left: ${displayName} (${userId})`);
        
        this.state.players.delete(displayName);
        this.emitToRenderer('log:player-left', { displayName, userId, timestamp });
        this.emit('player-left', { displayName, userId, timestamp });
    }
  }

  private emitToRenderer(channel: string, data: unknown) {
    const windows = BrowserWindow.getAllWindows();
    for (const win of windows) {
      if (!win.isDestroyed()) {
        win.webContents.send(channel, data);
      }
    }
  }
  public getPlayers(): PlayerJoinedEvent[] {
      return Array.from(this.state.players.values());
  }
}

export const logWatcherService = new LogWatcherService();

export function setupLogWatcherHandlers() {
  ipcMain.handle('log-watcher:start', (event) => {
    // Pass the sender window so we can sync state specifically to it
    const win = BrowserWindow.fromWebContents(event.sender);
    logWatcherService.start(win || undefined);
    return { success: true };
  });

  ipcMain.handle('log-watcher:stop', () => {
    logWatcherService.stop();
    return { success: true };
  });
  
  // Removed global auto-start to ensure we sync on request
}

/**
 * VRChat Pipeline WebSocket Service
 * 
 * Connects to VRChat's real-time WebSocket API for live event streaming.
 * Based on: https://vrchat.community/websocket
 * Reference: VRCX implementation (reference repos/VRCX/src/service/websocket.js)
 * 
 * Events supported:
 * - Notifications (invites, friend requests)
 * - Friend status changes (online, offline, location)
 * - User updates (current user profile changes)
 * - Group events (member updates, role changes, join/leave)
 */

import { ipcMain, BrowserWindow } from 'electron';
import log from 'electron-log';
const logger = log.scope('PipelineService');
import WebSocket from 'ws';
import { getVRChatClient, isAuthenticated } from './AuthService';

// ============================================
// CONSTANTS
// ============================================

const PIPELINE_URL = 'wss://pipeline.vrchat.cloud';
const RECONNECT_DELAY_MS = 5000;
const MAX_RECONNECT_ATTEMPTS = 10;

// ============================================
// STATE
// ============================================

let webSocket: WebSocket | null = null;
let reconnectAttempts = 0;
let reconnectTimeout: ReturnType<typeof setTimeout> | null = null;
let lastMessageData = '';
let isConnecting = false;
let isManualDisconnect = false;

// ============================================
// TYPES
// ============================================

/** Pipeline event types from VRChat WebSocket API */
export type PipelineEventType =
  // Notification Events
  | 'notification'
  | 'notification-v2'
  | 'notification-v2-update'
  | 'notification-v2-delete'
  | 'see-notification'
  | 'hide-notification'
  | 'response-notification'
  | 'clear-notification'
  // Friend Events
  | 'friend-add'
  | 'friend-delete'
  | 'friend-online'
  | 'friend-active'
  | 'friend-offline'
  | 'friend-update'
  | 'friend-location'
  // User Events
  | 'user-update'
  | 'user-location'
  | 'user-badge-assigned'
  | 'user-badge-unassigned'
  | 'content-refresh'
  // Group Events
  | 'group-joined'
  | 'group-left'
  | 'group-member-updated'
  | 'group-role-updated';

interface PipelineMessage {
  type: PipelineEventType;
  content: unknown;
  err?: string;
}

interface PipelineEvent {
  type: PipelineEventType;
  content: Record<string, unknown>;
  timestamp: string;
}

// ============================================
// AUTH TOKEN FETCHING
// ============================================

/**
 * Fetches the auth token required for WebSocket connection.
 * VRChat requires a call to GET /auth to retrieve the WebSocket token.
 */
async function fetchAuthToken(): Promise<string | null> {
  try {
    const client = getVRChatClient();
    if (!client) {
      log.warn('[Pipeline] No VRChat client available for auth token fetch');
      return null;
    }

    // The VRChat SDK should have a method to get auth info
    // Looking at VRCX, they call: request('auth', { method: 'GET' })
    // which returns { ok: true, token: "authcookie_..." }
    
    // Try using the SDK's internal methods
    const clientAny = client as Record<string, unknown>;
    
    // Strategy 1: Try getAuth if available
    if (typeof clientAny.getAuth === 'function') {
      log.debug('[Pipeline] Using getAuth method');
      const response = await (clientAny.getAuth as () => Promise<{ data?: { ok?: boolean; token?: string } }>)();
      const data = response?.data;
      if (data?.ok && data?.token) {
        log.info('[Pipeline] Got auth token via getAuth');
        return data.token;
      }
    }

    // Strategy 2: Try direct API call if client supports it
    if (typeof clientAny.get === 'function') {
      log.debug('[Pipeline] Using client.get for auth endpoint');
      const response = await (clientAny.get as (path: string) => Promise<{ data?: { ok?: boolean; token?: string } }>)('auth');
      const data = response?.data;
      if (data?.ok && data?.token) {
        log.info('[Pipeline] Got auth token via client.get');
        return data.token;
      }
    }

    // Strategy 3: Try to extract from the client's cookie jar
    // The auth token for WebSocket is the same as the auth cookie value
    if (clientAny.jar || clientAny.cookieJar) {
      log.debug('[Pipeline] Attempting to extract token from cookie jar');
      const jar = (clientAny.jar || clientAny.cookieJar) as { 
        getCookiesSync?: (url: string) => Array<{ key?: string; name?: string; value?: string }>;
        _jar?: { getCookiesSync?: (url: string) => Array<{ key?: string; name?: string; value?: string }> };
      };
      
      let cookies: Array<{ key?: string; name?: string; value?: string }> = [];
      
      if (typeof jar.getCookiesSync === 'function') {
        cookies = jar.getCookiesSync('https://api.vrchat.cloud');
      } else if (jar._jar && typeof jar._jar.getCookiesSync === 'function') {
        cookies = jar._jar.getCookiesSync('https://api.vrchat.cloud');
      }
      
      const authCookie = cookies.find(c => (c.key || c.name) === 'auth');
      if (authCookie?.value) {
        // The token format is "authcookie_..." which is the cookie value
        log.info('[Pipeline] Got auth token from cookie jar');
        return `authcookie_${authCookie.value}`;
      }
    }

    // Strategy 4: Manual fetch using node-fetch or similar
    // This is a fallback - we make a direct HTTP request to the auth endpoint
    log.debug('[Pipeline] Fallback: Making direct HTTP request to /auth');
    
    // Get cookies from the client to include in the request
    // This requires the client to expose its cookie handling
    
    log.warn('[Pipeline] Could not obtain auth token - all strategies exhausted');
    return null;

  } catch (error: unknown) {
    const err = error as { message?: string };
    log.error('[Pipeline] Failed to fetch auth token:', err.message);
    return null;
  }
}

// ============================================
// WEBSOCKET CONNECTION
// ============================================

/**
 * Connects to the VRChat Pipeline WebSocket.
 */
async function connectWebSocket(): Promise<boolean> {
  if (webSocket !== null || isConnecting) {
    log.debug('[Pipeline] Already connected or connecting');
    return false;
  }

  if (!isAuthenticated()) {
    log.warn('[Pipeline] Cannot connect - not authenticated');
    return false;
  }

  isConnecting = true;
  isManualDisconnect = false;

  try {
    const token = await fetchAuthToken();
    
    if (!token) {
      log.error('[Pipeline] Cannot connect - no auth token available');
      isConnecting = false;
      return false;
    }

    const url = `${PIPELINE_URL}/?authToken=${token}`;
    log.info('[Pipeline] Connecting to VRChat Pipeline WebSocket...');

    const socket = new WebSocket(url, {
      headers: {
        'User-Agent': 'VRChatGroupGuard/1.0.0 (admin@groupguard.app)'
      }
    });

    socket.onopen = () => {
      log.info('[Pipeline] WebSocket connected successfully');
      isConnecting = false;
      reconnectAttempts = 0;
      webSocket = socket;
      
      // Notify renderer that pipeline is connected
      emitToRenderer('pipeline:connected', { connected: true });
    };

    socket.onclose = (event: WebSocket.CloseEvent) => {
      log.info(`[Pipeline] WebSocket closed: code=${event.code}, reason=${event.reason}`);
      
      if (webSocket === socket) {
        webSocket = null;
      }
      
      isConnecting = false;
      
      // Notify renderer
      emitToRenderer('pipeline:disconnected', { 
        code: event.code, 
        reason: event.reason,
        willReconnect: !isManualDisconnect && reconnectAttempts < MAX_RECONNECT_ATTEMPTS
      });

      // Auto-reconnect if not manually disconnected
      if (!isManualDisconnect && isAuthenticated()) {
        scheduleReconnect();
      }
    };

    socket.onerror = (error: WebSocket.ErrorEvent) => {
      log.error('[Pipeline] WebSocket error:', error.message || 'Unknown error');
      emitToRenderer('pipeline:error', { message: error.message || 'WebSocket error' });
    };

    socket.onmessage = (event: WebSocket.MessageEvent) => {
      try {
        handleMessage(event.data.toString());
      } catch (err: unknown) {
        const error = err as { message?: string };
        log.error('[Pipeline] Error handling message:', error.message);
      }
    };

    return true;

  } catch (error: unknown) {
    const err = error as { message?: string };
    log.error('[Pipeline] Connection error:', err.message);
    isConnecting = false;
    scheduleReconnect();
    return false;
  }
}

/**
 * Schedules a reconnection attempt with exponential backoff.
 */
function scheduleReconnect(): void {
  if (reconnectTimeout !== null) {
    clearTimeout(reconnectTimeout);
  }

  if (reconnectAttempts >= MAX_RECONNECT_ATTEMPTS) {
    log.warn('[Pipeline] Max reconnect attempts reached, giving up');
    emitToRenderer('pipeline:reconnect-failed', { 
      attempts: reconnectAttempts 
    });
    return;
  }

  reconnectAttempts++;
  const delay = RECONNECT_DELAY_MS * Math.min(reconnectAttempts, 5); // Cap at 25s
  
  log.info(`[Pipeline] Scheduling reconnect attempt ${reconnectAttempts}/${MAX_RECONNECT_ATTEMPTS} in ${delay}ms`);
  
  reconnectTimeout = setTimeout(() => {
    reconnectTimeout = null;
    if (isAuthenticated() && !webSocket && !isConnecting && !isManualDisconnect) {
      connectWebSocket();
    }
  }, delay);
}

/**
 * Disconnects the WebSocket.
 */
function disconnectWebSocket(): void {
  isManualDisconnect = true;
  
  if (reconnectTimeout !== null) {
    clearTimeout(reconnectTimeout);
    reconnectTimeout = null;
  }

  if (webSocket !== null) {
    log.info('[Pipeline] Disconnecting WebSocket...');
    try {
      webSocket.close(1000, 'Manual disconnect');
    } catch (err) {
      log.warn('[Pipeline] Error closing WebSocket:', err);
    }
    webSocket = null;
  }
  
  reconnectAttempts = 0;
  isConnecting = false;
}

// ============================================
// MESSAGE HANDLING
// ============================================

/**
 * Handles incoming WebSocket messages.
 */
function handleMessage(data: string): void {
  // Dedupe identical messages (VRChat sometimes sends duplicates)
  if (lastMessageData === data) {
    return;
  }
  lastMessageData = data;

  let message: PipelineMessage;
  
  try {
    message = JSON.parse(data);
    
    // VRChat double-encodes content as a JSON string
    if (typeof message.content === 'string') {
      try {
        message.content = JSON.parse(message.content);
      } catch {
        // Content is not JSON, keep as string
      }
    }
  } catch {
    log.warn('[Pipeline] Failed to parse message:', data.substring(0, 100));
    return;
  }

  // Handle errors from the pipeline
  if (message.err) {
    log.error('[Pipeline] Server error:', message.err);
    emitToRenderer('pipeline:server-error', { error: message.err });
    return;
  }

  // Log the event
  log.debug(`[Pipeline] Event: ${message.type}`, JSON.stringify(message.content).substring(0, 200));

  // Create a standardized event
  const event: PipelineEvent = {
    type: message.type,
    content: message.content as Record<string, unknown>,
    timestamp: new Date().toISOString()
  };

  // Emit to renderer
  emitToRenderer('pipeline:event', event);

  // Handle specific event types that may need additional processing
  handleSpecificEvent(event);
}

/**
 * Handles specific event types that may need server-side processing.
 */
function handleSpecificEvent(event: PipelineEvent): void {
  switch (event.type) {
    case 'group-member-updated':
      log.info('[Pipeline] Group member updated:', event.content);
      break;
      
    case 'group-role-updated':
      log.info('[Pipeline] Group role updated:', event.content);
      break;
      
    case 'group-joined':
    case 'group-left':
      log.info(`[Pipeline] Group ${event.type}:`, event.content);
      break;

    case 'notification':
    case 'notification-v2':
      log.info('[Pipeline] Notification received:', event.content);
      break;

    case 'friend-online':
    case 'friend-offline':
    case 'friend-location':
      log.debug(`[Pipeline] Friend ${event.type}:`, event.content);
      break;

    case 'user-update':
      log.info('[Pipeline] Current user updated:', event.content);
      break;

    default:
      // Other events are just forwarded to renderer
      break;
  }
}

// ============================================
// IPC BRIDGE
// ============================================

/**
 * Emits an event to all renderer windows.
 */
function emitToRenderer(channel: string, data: unknown): void {
  const windows = BrowserWindow.getAllWindows();
  for (const window of windows) {
    if (!window.isDestroyed()) {
      window.webContents.send(channel, data);
    }
  }
}

/**
 * Sets up IPC handlers for the Pipeline service.
 */
export function setupPipelineHandlers(): void {
  // Connect to pipeline
  ipcMain.handle('pipeline:connect', async () => {
    log.info('[Pipeline] Connect requested');
    const success = await connectWebSocket();
    return { success, connected: webSocket !== null };
  });

  // Disconnect from pipeline
  ipcMain.handle('pipeline:disconnect', () => {
    log.info('[Pipeline] Disconnect requested');
    disconnectWebSocket();
    return { success: true };
  });

  // Get connection status
  ipcMain.handle('pipeline:status', () => {
    return {
      connected: webSocket !== null && webSocket.readyState === WebSocket.OPEN,
      connecting: isConnecting,
      reconnectAttempts
    };
  });

  // Force reconnect
  ipcMain.handle('pipeline:reconnect', async () => {
    log.info('[Pipeline] Reconnect requested');
    disconnectWebSocket();
    isManualDisconnect = false;
    reconnectAttempts = 0;
    const success = await connectWebSocket();
    return { success };
  });
}

// ============================================
// LIFECYCLE
// ============================================

/**
 * Call this when the user logs in to auto-connect to the pipeline.
 */
export function onUserLoggedIn(): void {
  log.info('[Pipeline] User logged in, connecting to pipeline...');
  // Small delay to ensure auth is fully set up
  setTimeout(() => {
    connectWebSocket();
  }, 1000);
}

/**
 * Call this when the user logs out to disconnect from the pipeline.
 */
export function onUserLoggedOut(): void {
  log.info('[Pipeline] User logged out, disconnecting from pipeline');
  disconnectWebSocket();
}

/**
 * Check if the pipeline is currently connected.
 */
export function isPipelineConnected(): boolean {
  return webSocket !== null && webSocket.readyState === WebSocket.OPEN;
}

import { ipcMain } from 'electron';
import log from 'electron-log';
const logger = log.scope('UserService');
import { getVRChatClient } from './AuthService';

// Simple in-memory cache
// Map<userId, { data: UserData, timestamp: number }>
const userCache = new Map<string, { data: any; timestamp: number }>();
const CACHE_TTL = 5 * 60 * 1000; // 5 minutes

export function setupUserHandlers() {
  
  // Get User Profile
  ipcMain.handle('users:get', async (_event, { userId }: { userId: string }) => {
    try {
      if (!userId) throw new Error("User ID is required");

      const client = getVRChatClient();
      if (!client) throw new Error("Not authenticated");

      // Check cache
      const cached = userCache.get(userId);
      if (cached && (Date.now() - cached.timestamp < CACHE_TTL)) {
        logger.debug(`Serving user ${userId} from cache`);
        return { success: true, user: cached.data };
      }

      log.info(`Fetching user ${userId} from API`);
      // Use Object syntax as verified in GroupService fixes
      const response = await client.getUser({ path: { userId } });
      
      if (response.error) {
          log.error('getUser returned error:', response.error);
          throw response.error;
      }
      
      // Update cache
      if (response.data) {
          userCache.set(userId, { data: response.data, timestamp: Date.now() });
      }

      return { success: true, user: response.data };

    } catch (error: any) {
      log.error(`Failed to fetch user ${userId}:`, error);
      return { success: false, error: error.message || 'Failed to fetch user' };
    }
  });

  // Clear cache for a user (useful if we get an update via WS)
  ipcMain.handle('users:clear-cache', async (_event, { userId }: { userId: string }) => {
      if (userId) {
          userCache.delete(userId);
      } else {
          userCache.clear();
      }
      return { success: true };
  });
}

