import React, { useEffect, useState } from 'react';
import { Modal } from '../../../components/ui/Modal';
import { GlassPanel } from '../../../components/ui/GlassPanel';
import { useUserProfileStore } from '../../../stores/userProfileStore';
import type { VRChatUser } from '../../../types/electron';

export const UserProfileDialog: React.FC = () => {
    const { isOpen, userId, closeProfile } = useUserProfileStore();
    const [user, setUser] = useState<VRChatUser | null>(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        if (isOpen && userId) {
            setLoading(true);
            setError(null);
            setUser(null);
            
            window.electron.getUser(userId)
                .then(res => {
                    if (res.success && res.user) {
                        setUser(res.user);
                    } else {
                        setError(res.error || 'Failed to load user');
                    }
                })
                .catch(err => setError(String(err)))
                .finally(() => setLoading(false));
        }
    }, [isOpen, userId]);

    if (!isOpen) return null;

    const getStatusColor = (status?: string) => {
        switch (status) {
            case 'active': return '#5cc9f5'; // Blue
            case 'join me': return '#42b983'; // Blue-ish
            case 'busy': return '#ab1a1a'; // Red
            default: return '#888';
        }
    };
    
    // Status text (VRChat "status" is often "active", "join me", etc. "state" is offline/online?)
    // Actually VRChat API is a bit complex here. statusDescription is the custom text.
    // status is "active", "join me", "ask me", "busy", "offline".

    return (
        <Modal 
            isOpen={isOpen} 
            onClose={closeProfile} 
            title={user?.displayName || 'User Profile'}
            width="600px"
        >
            {loading ? (
                <div style={{ padding: '40px', textAlign: 'center', color: '#888' }}>
                    Loading profile...
                </div>
            ) : error ? (
                <div style={{ padding: '20px', textAlign: 'center', color: '#ff4444' }}>
                    {error}
                </div>
            ) : user ? (
                <div style={{ padding: '10px' }}>
                    {/* Header: Avatar & Basic Info */}
                    <div style={{ display: 'flex', gap: '20px', marginBottom: '20px' }}>
                        <div style={{ position: 'relative' }}>
                            <img 
                                src={user.profilePicOverride || user.currentAvatarImageUrl || user.userIcon} 
                                alt={user.displayName}
                                style={{ 
                                    width: '120px', 
                                    height: '120px', 
                                    borderRadius: '12px', 
                                    objectFit: 'cover',
                                    border: `2px solid ${getStatusColor(user.status)}`
                                }} 
                            />
                            <div style={{ position: 'absolute', bottom: '-10px', right: '-10px', display: 'flex', alignItems: 'center', gap: '5px' }}>
                                {/* Age Verified Badge */}
                                {(user.ageVerified || user.tags?.includes('system_age_verified')) && (
                                    <div style={{
                                        background: '#fbbf24', // Amber/Gold
                                        color: '#000',
                                        padding: '2px 6px',
                                        borderRadius: '8px',
                                        fontSize: '0.75rem',
                                        fontWeight: '800',
                                        border: '2px solid rgba(0,0,0,0.5)'
                                    }}>
                                        18+
                                    </div>
                                )}
                                
                                <div style={{
                                    background: getStatusColor(user.status),
                                    color: 'white',
                                    padding: '2px 8px',
                                    borderRadius: '10px',
                                    fontSize: '0.8rem',
                                    fontWeight: 'bold',
                                    border: '2px solid #1a1b1e' // Match background? Or dark border
                                }}>
                                    {user.status || 'Offline'}
                                </div>
                            </div>
                        </div>
                        
                        <div style={{ flex: 1 }}>
                            <h2 style={{ margin: '0 0 5px 0', fontSize: '1.8rem', color: '#fff' }}>
                                {user.displayName}
                            </h2>
                            <div style={{ color: '#aaa', fontSize: '0.9rem', marginBottom: '10px' }}>
                                {user.statusDescription}
                            </div>
                            
                            <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
                                {user.tags?.map(tag => (
                                    <span key={tag} style={{ 
                                        background: 'rgba(255,255,255,0.1)', 
                                        padding: '2px 8px', 
                                        borderRadius: '4px',
                                        fontSize: '0.8rem',
                                        color: '#ccc'
                                    }}>
                                        {tag.replace('system_', '').replace(/_/g, ' ')}
                                    </span>
                                ))}
                            </div>
                        </div>
                    </div>

                    {/* Bio */}
                    <GlassPanel style={{ padding: '15px', marginBottom: '15px' }}>
                        <h4 style={{ marginTop: 0, color: '#888', textTransform: 'uppercase', fontSize: '0.8rem' }}>Bio</h4>
                        <div style={{ whiteSpace: 'pre-wrap', lineHeight: '1.5', color: '#eee' }}>
                            {user.bio || 'No bio available.'}
                        </div>
                        
                        {user.bioLinks && user.bioLinks.length > 0 && (
                            <div style={{ marginTop: '10px', display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
                                {user.bioLinks.map(link => (
                                    <a  key={link} 
                                        href={link} 
                                        target="_blank" 
                                        rel="noopener noreferrer"
                                        style={{ color: '#5cc9f5', textDecoration: 'none', fontSize: '0.9rem' }}
                                    >
                                        {link}
                                    </a>
                                ))}
                            </div>
                        )}
                    </GlassPanel>

                    {/* Details Grid */}
                    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px' }}>
                        <GlassPanel style={{ padding: '15px' }}>
                            <div style={{ color: '#888', fontSize: '0.8rem' }}>Last Login</div>
                            <div>{user.last_login ? new Date(user.last_login).toLocaleDateString() : 'Unknown'}</div>
                        </GlassPanel>
                        <GlassPanel style={{ padding: '15px' }}>
                            <div style={{ color: '#888', fontSize: '0.8rem' }}>Date Joined</div>
                            <div>{user.date_joined ? new Date(user.date_joined).toLocaleDateString() : 'Unknown'}</div>
                        </GlassPanel>
                        <GlassPanel style={{ padding: '15px' }}>
                            <div style={{ color: '#888', fontSize: '0.8rem' }}>Trust Rank</div>
                            <div>{getTrustRank(user.tags)}</div>
                        </GlassPanel>
                         <GlassPanel style={{ padding: '15px' }}>
                            <div style={{ color: '#888', fontSize: '0.8rem' }}>Location</div>
                            <div>{user.location || user.state || 'Unknown'}</div>
                        </GlassPanel>
                    </div>

                </div>
            ) : null}
        </Modal>
    );
};

// Helper to deduce rank from tags because VRChat API doesn't send "trustRank" field directly typically, 
// unless we inspect tags like 'system_trust_veteran'.
function getTrustRank(tags: string[] = []): string {
    if (tags.includes('system_trust_legend')) return 'Legendary';
    if (tags.includes('system_trust_veteran')) return 'Veteran';
    if (tags.includes('system_trust_trusted')) return 'Trusted';
    if (tags.includes('system_trust_known')) return 'Known';
    if (tags.includes('system_trust_basic')) return 'User';
    if (tags.includes('system_probation')) return 'Visitor (Probation)'; // or similar
    // Default fallback
    return 'Visitor'; 
}

import { useEffect, useRef } from 'react';
import { useInstanceMonitorStore, type LivePlayerInfo } from '../stores/instanceMonitorStore';
import { useGroupStore } from '../stores/groupStore';

export function useInstanceMonitorInit(isAuthenticated: boolean) {
  const { addPlayer, removePlayer, setWorldId, setInstanceInfo, setWorldName, setInstanceImage, clearInstance, clearLiveScan } = useInstanceMonitorStore();
  const { isRoamingMode } = useGroupStore();

  // Clear live scan history when exiting roaming mode
  const prevRoamingRef = useRef(isRoamingMode);
  useEffect(() => {
    if (prevRoamingRef.current && !isRoamingMode) {
        clearLiveScan();
    }
    prevRoamingRef.current = isRoamingMode;
  }, [isRoamingMode, clearLiveScan]);

  useEffect(() => {
    if (!isAuthenticated) return;

    console.log('[InstanceMonitor] Initializing Log Watcher...');
    // Start watching logs
    window.electron.logWatcher.start();

    // Setup event listeners
    const cleanupJoined = window.electron.logWatcher.onPlayerJoined((event) => {
      const player: LivePlayerInfo = {
        displayName: event.displayName,
        userId: event.userId,
        joinTime: new Date(event.timestamp).getTime()
      };
      addPlayer(player);
    });

    const cleanupLeft = window.electron.logWatcher.onPlayerLeft((event) => {
      removePlayer(event.displayName);
    });

    const cleanupLocation = window.electron.logWatcher.onLocation(async (event) => {
      // New location detected, clear previous instance data
      clearInstance();
      setWorldId(event.worldId);
      
      const eventAny = event as any;
      if (eventAny.instanceId && eventAny.location) {
          setInstanceInfo(eventAny.instanceId, eventAny.location);
      }
      
      // Fetch world details if we can (to fix "Unknown World")
      try {
        const result = await window.electron.getWorld(event.worldId);
        if (result.success && result.world) {
            setWorldName(result.world.name);
            // Assuming world object has imageUrl or thumbnailImageUrl
            const img = (result.world as any).imageUrl || (result.world as any).thumbnailImageUrl;
            if (img) setInstanceImage(img);
        }
      } catch (e) {
        console.error('Failed to fetch world name', e);
      }
    });

    const cleanupWorldName = window.electron.logWatcher.onWorldName((event) => {
      setWorldName(event.name);
    });

    return () => {
      cleanupJoined();
      cleanupLeft();
      cleanupLocation();
      cleanupWorldName();
      window.electron.logWatcher.stop();
    };

  }, [isAuthenticated, addPlayer, removePlayer, setWorldId, setWorldName, clearInstance, setInstanceInfo, setInstanceImage]);
}

// User type from VRChat API
export interface VRChatUser {
  id: string;
  username: string;
  displayName: string;
  userIcon: string;
  bio?: string;
  currentAvatarImageUrl?: string;
  currentAvatarThumbnailImageUrl?: string;
  status?: string;
  statusDescription?: string;
  state?: string;
  tags?: string[];
  developerType?: string;
  last_login?: string;
  last_platform?: string;
  allowAvatarCopying?: boolean;
  isFriend?: boolean;
  friendKey?: string;
  last_activity?: string;
  bioLinks?: string[];
  date_joined?: string;
  location?: string;
  profilePicOverride?: string;
  friendKey?: string;
  isFriend?: boolean;
  statusHistory?: string[];
  ageVerified?: boolean;
  // Add additional fields as needed
}

// Group type from VRChat API  
export interface VRChatGroup {
  id: string;
  name: string;
  shortCode: string;
  discriminator?: string;
  description?: string;
  iconUrl?: string;
  iconId?: string;
  bannerUrl?: string;
  bannerId?: string;
  privacy?: string;
  ownerId: string;
  rules?: string;
  links?: string[];
  languages?: string[];
  memberCount: number;
  memberCountSyncedAt?: string;
  isVerified?: boolean;
  joinState?: string;
  tags?: string[];
  galleries?: unknown[];
  createdAt?: string;
  onlineMemberCount?: number;
  membershipStatus?: string;
  myMember?: unknown;
}

export interface LoginCredentials {
  username: string;
  password: string;
  rememberMe?: boolean;
}

export interface LoginResult {
  success: boolean;
  user?: VRChatUser;
  requires2FA?: boolean;
  twoFactorMethods?: string[];
  error?: string;
}

export interface AutoLoginResult {
  success: boolean;
  user?: VRChatUser;
  requires2FA?: boolean;
  noCredentials?: boolean;
  error?: string;
}

export interface SessionResult {
  isLoggedIn: boolean;
  user?: VRChatUser;
}

export interface LogoutOptions {
  clearSaved?: boolean;
}

export interface GroupsResult {
  success: boolean;
  groups?: VRChatGroup[];
  error?: string;
}

export interface AuditLogsResult {
  success: boolean;
  logs?: unknown[];
  error?: string;
}

export interface GroupMember {
  id: string; // "gmem_..."
  groupId: string;
  userId: string;
  isRepresenting: boolean;
  roleIds: string[];
  mangerNotes?: string;
  membershipStatus: string; // "member" | "admin" | "owner"
  visibility: string;
  joinedAt?: string;
  bannedAt?: string;
  user: VRChatUser;
}

export interface GroupRequest {
  id: string;
  user: VRChatUser;
  groupId: string;
  userId: string;
  createdAt: string;
}

export interface GroupBan {
  id: string;
  user: VRChatUser;
  groupId: string;
  userId: string;
  createdAt: string; // or bannedAt
}

export interface VRChatInstance {
  id?: string; // Sometimes missing in group instances - use location or instanceId
  instanceId?: string;
  name?: string; // Instance number like "86267"
  displayName?: string; // Group display name
  location?: string; // Full location string: worldId:instanceId
  worldId?: string;
  
  // User counts
  n_users?: number;
  userCount?: number;
  memberCount?: number;
  capacity?: number;
  
  // Instance properties
  ownerId?: string;
  type?: string; // "group", "public", etc.
  groupAccessType?: string; // "public", "plus", "members" for group instances
  ageGate?: boolean; // 18+ age verification required
  region?: string;
  shortName?: string;
  
  // World data (nested from API)
  world?: {
      id?: string;
      name?: string;
      authorId?: string;
      authorName?: string;
      description?: string;
      capacity?: number;
      imageUrl?: string;
      thumbnailImageUrl?: string;
  };

  // Group data (nested from API)
  group?: {
      id?: string;
      name?: string;
      shortCode?: string;
      discriminator?: string;
  };
}

// Pipeline WebSocket Event Types
export type PipelineEventType =
  // Notification Events
  | 'notification'
  | 'notification-v2'
  | 'notification-v2-update'
  | 'notification-v2-delete'
  | 'see-notification'
  | 'hide-notification'
  | 'response-notification'
  | 'clear-notification'
  // Friend Events
  | 'friend-add'
  | 'friend-delete'
  | 'friend-online'
  | 'friend-active'
  | 'friend-offline'
  | 'friend-update'
  | 'friend-location'
  // User Events
  | 'user-update'
  | 'user-location'
  | 'user-badge-assigned'
  | 'user-badge-unassigned'
  | 'content-refresh'
  // Group Events
  | 'group-joined'
  | 'group-left'
  | 'group-member-updated'
  | 'group-role-updated';

export interface PipelineEvent {
  type: PipelineEventType;
  content: Record<string, unknown>;
  timestamp: string;
}

export interface AutoModRule {
    id: number;
    name: string;
    enabled: boolean;
    type: 'AGE_CHECK' | 'TRUST_CHECK' | 'KEYWORD_BLOCK' | 'WHITELIST_CHECK' | 'BAN_EVASION_CHECK';
    config: string;
    actionType: 'REJECT' | 'AUTO_BLOCK' | 'NOTIFY_ONLY';
    createdAt?: string;
}

export interface ElectronAPI {
  log: (level: 'info' | 'warn' | 'error', message: string) => void;
  getVersion: () => string;
  
  // Auth API
  login: (credentials: LoginCredentials) => Promise<LoginResult>;
  verify2fa: (data: { code: string }) => Promise<LoginResult>;
  checkSession: () => Promise<SessionResult>;
  autoLogin: () => Promise<AutoLoginResult>;
  hasSavedCredentials: () => Promise<boolean>;
  loadSavedCredentials: () => Promise<{ username: string; password: string; authCookie?: string } | null>;
  logout: (options?: LogoutOptions) => Promise<{ success: boolean }>;
  clearCredentials: () => Promise<{ success: boolean }>;
  
  // Groups API
  getMyGroups: () => Promise<GroupsResult>;
  getGroupDetails: (groupId: string) => Promise<{ success: boolean; group?: VRChatGroup; error?: string }>;
  getGroupMembers: (groupId: string, offset?: number, n?: number) => Promise<{ success: boolean; members?: GroupMember[]; error?: string }>;
  getGroupRequests: (groupId: string) => Promise<{ success: boolean; requests?: GroupRequest[]; error?: string }>;
  getGroupBans: (groupId: string) => Promise<{ success: boolean; bans?: GroupBan[]; error?: string }>;
  getGroupInstances: (groupId: string) => Promise<{ success: boolean; instances?: VRChatInstance[]; error?: string }>;
  banUser: (groupId: string, userId: string) => Promise<{ success: boolean; error?: string }>;


  
  // Audit API
  getGroupAuditLogs: (groupId: string) => Promise<AuditLogsResult>;

  // Worlds API
  getWorld: (worldId: string) => Promise<{ success: boolean; world?: { id: string; name: string; capacity?: number; imageUrl?: string; authorName?: string }; error?: string }>;

  // User API
  getUser: (userId: string) => Promise<{ success: boolean; user?: VRChatUser; error?: string }>;
  clearUserCache: (userId?: string) => Promise<{ success: boolean }>;

  // Pipeline (WebSocket) API for real-time events
  pipeline: {
    connect: () => Promise<{ success: boolean; connected: boolean }>;
    disconnect: () => Promise<{ success: boolean }>;
    status: () => Promise<{ connected: boolean; connecting: boolean; reconnectAttempts: number }>;
    reconnect: () => Promise<{ success: boolean }>;
    
    // Event listeners - return unsubscribe function
    onEvent: (callback: (event: PipelineEvent) => void) => () => void;
    onConnected: (callback: (data: { connected: boolean }) => void) => () => void;
    onDisconnected: (callback: (data: { code: number; reason: string; willReconnect: boolean }) => void) => () => void;
    onError: (callback: (data: { message: string }) => void) => () => void;
  };

  // Log Watcher API for live log events
  logWatcher: {
    start: () => Promise<{ success: boolean }>;
    stop: () => Promise<{ success: boolean }>;
    onPlayerJoined: (callback: (event: { displayName: string; userId?: string; timestamp: string }) => void) => () => void;
    onPlayerLeft: (callback: (event: { displayName: string; userId?: string; timestamp: string }) => void) => () => void;
    onLocation: (callback: (event: { worldId: string; timestamp: string }) => void) => () => void;
    onWorldName: (callback: (event: { name: string; timestamp: string }) => void) => () => void;
  };

  // Database API for local logging
  database: {
      getSessions: (groupId?: string) => Promise<unknown[]>;
      getSessionEvents: (filename: string) => Promise<unknown[]>;
      clearSessions: () => Promise<boolean>;
      rallyFromSession: (filename: string) => Promise<{
          success: boolean;
          invited?: number;
          failed?: number;
          total?: number;
          error?: string;
          errors?: string[];
      }>;
      onRallyProgress: (callback: (data: { sent: number; failed: number; total: number; done?: boolean }) => void) => () => void;
  };

  // Storage API
  storage: {
      getStatus: () => Promise<{ configured: boolean; path: string; defaultPath: string }>;
      selectFolder: () => Promise<string | null>;
      setPath: (path: string) => Promise<boolean>;
  };

  // Instance Presence API
  instance: {
      // NEW LIVE OPS API
      scanSector: (groupId: string) => Promise<any[]>;
      recruitUser: (groupId: string, userId: string) => Promise<{ success: boolean; error?: string }>;
      kickUser: (groupId: string, userId: string) => Promise<{ success: boolean; error?: string }>;
      // rallyForces: (groupId: string) => Promise<{ success: boolean; count?: number; error?: string }>;
      getRallyTargets: (groupId: string) => Promise<{ success: boolean; targets?: any[]; error?: string }>;
      inviteToCurrent: (userId: string) => Promise<{ success: boolean; error?: string }>;
      closeInstance: () => Promise<{ success: boolean; error?: string }>;
      getInstanceInfo: () => Promise<{ success: boolean; worldId?: string; instanceId?: string; name?: string; imageUrl?: string; error?: string }>;
      onEntityUpdate: (callback: (entity: any) => void) => () => void;
      
      getCurrentGroup: () => Promise<string | null>;
      onGroupChanged: (callback: (groupId: string | null) => void) => () => void;
  };

  // Updater API
  updater: {
      onUpdateAvailable: (callback: () => void) => () => void;
      onUpdateDownloaded: (callback: () => void) => () => void;
      quitAndInstall: () => void;
      checkStatus: () => Promise<boolean>;
  };

  // Window Controls
  minimize: () => void;
  maximize: () => void;
  close: () => void;

  // AutoMod API
  automod: {
      getRules: () => Promise<AutoModRule[]>;
      saveRule: (rule: AutoModRule) => Promise<AutoModRule>;
      deleteRule: (ruleId: number) => Promise<boolean>;
      checkUser: (user: any) => Promise<{ action: 'ALLOW' | 'REJECT' | 'AUTO_BLOCK' | 'NOTIFY_ONLY'; reason?: string; ruleName?: string }>;
  };
}

declare global {
  interface Window {
    electron: ElectronAPI;
  }
}
